// Helper function to get entry metadata by ID
export const getEntryMetadata = (id) => {
  return entriesManifest.find(entry => entry.id === parseInt(id));
};

// Helper function to get all unique tags
export const getAllTags = () => {
  const allTags = new Set();
  entriesManifest.forEach(entry => {
    if (entry.tags) {
      entry.tags.forEach(tag => allTags.add(tag));
    }
  });
  return Array.from(allTags).sort();
};

// Helper function to get all unique locations
export const getAllLocations = () => {
  const allLocations = new Set();
  entriesManifest.forEach(entry => {
    if (entry.location) {
      allLocations.add(entry.location);
    }
  });
  return Array.from(allLocations).sort();
};
